mapper.select:
SELECT * FROM board


ORDER BY ${sort} DESC
LIMIT #{offset}, #{limit}


LIMIT #{offset}, #{limit}

mapper.update:
UPDATE board
SET board_title=#{boardTitle}, board_content=#{boardContent}
WHERE board_no=#{boardNo}

update board b 
set b.bc_count = (select count(bc_no)
from board_comment
where board_no = ${no}
and bc_delete = "n")
where b.board_no = ${no};
